package ejercicios;

public class Programa {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int contador = 1;
		do {
			System.out.println("a");
			contador++;
			
		}while(contador<=5);
		
		for(int contador2=1;contador2<=5;contador2++) {
			System.out.println("a");
		}
		contador = 1;
		while(contador<=5) {
			System.out.println("a");
			contador++;
		}
	}

}
